window.addEventListener("message",function(event){if(event.source!==window)return;var message=event.data;if(typeof message==="object"&&message!==null&&message.cmd==="injectContent"){var p=document.createElement("p");p.textContent="Hello, world!";document.body.appendChild(p)}});var pageTitle=document.title;chrome.runtime.sendMessage({cmd:"pageData",data:pageTitle}).catch(console.error);