"use strict";window.addEventListener("message",t=>{if(t.source!==window)return;const e=t.data;if(typeof e=="object"&&e!==null&&e.cmd==="injectContent"){const n=document.createElement("p");n.textContent="Hello, world!",document.body.appendChild(n)}});const pageTitle=document.title;chrome.runtime.sendMessage({cmd:"pageData",data:pageTitle}).catch(console.error);
